// Utility functions for styled-components
export const spacing = (value) => {
  const token = tokens.base?.spacing?.[value];
  return token || value;
};

export const color = (path) => {
  const keys = path.split('.');
  let value = tokens;
  for (const key of keys) {
    value = value?.[key];
  }
  return value || path;
};

